import { Component } from "react";
import queryString from 'query-string';
import ApiService from "../../ApiService";

class WebBoardModifyComponent extends Component{
    constructor(props)
    {
        super(props);

        const {bno} = queryString.parse(this.props.location.search);

        this.state = {
            bno : bno,
            board : {
                bno : bno,
                title : "",
                content : "",
                writer : "",
                regdate : ""
            }
        }
    
    }

    componentDidMount(){
        this.reloadModifyWebBoard(this.state.bno);
    }

    reloadModifyWebBoard(bno){
        ApiService.fetchModifyWebBoard(bno)
                    .then(res=>{
                        this.setState({
                            board : res.data
                        });
                    })
                    .catch(err=>{
                        console.log("fetchModifyWebBoard() error!", err);
                    });

        console.log(this.state.board);
    }

    render(){
        return (
            <>
            <h1>hello modify</h1>
            </>
        );
    }
}

export default WebBoardModifyComponent;
