import React, {Component} from 'react';
import ApiService from "../../ApiService";
import queryStirng from 'query-string';

class WebBoardListComponent extends Component{
    constructor(props){
        super(props);

        const p = queryStirng.parse(props.location.search).page;

        this.state = {
            boards : [],
            message : null,
            page : (p===undefined || p<1) ? 1 : p
        }
    }

    componentDidMount(){
        this.reloadWebBoardList(this.state.page);
    }

    reloadWebBoardList = (page)=>{
        ApiService.fetchWebBoards(page).then(res=>{
                                                    
                                                    this.setState({
                                                                    pageable : res.data,
                                                                    boards:res.data.result.content
                                                                    })

                                                    console.log("pageable.pageList is " + this.state.pageable.pageList);
                                        })
                                        .catch(err=>{
                                            console.log("reloadWebBoardList() Error!",err);                        
                                        });
    }

    render(){
        return(
            <>
            <div>
                <h2>WebBoard List</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Bno</th>
                            <th>Title</th>
                            <th>Wirter</th>
                            <th>Content</th>
                            <th>Regdate</th>
                            <th>Updatedate</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.boards.map( board=>
                                    <tr key={board.bno}>
                                        <td>{board.bno}</td>
                                        <td>{board.title}</td>
                                        <td>{board.writer}</td>
                                        <td>{board.content}</td>
                                        <td>{board.regdate}</td>
                                        <td>{board.updatedate}</td>
                                    </tr>
                                )
                        }
                    </tbody>
                </table>
            </div>
            <div>
                <ul>
                    {
                        this.state.pageable.pageList.map(p=>
                                                    <li>{p.pageNumber}</li>
                                                )
                    }
                </ul>
            </div>
            </>
        );
    }
}

export default WebBoardListComponent;
