import { Component } from "react";
import queryString from 'query-string';
import ApiService from "../../ApiService";
import change_date from "../../function/change_date";

class WebBoardModifyComponent extends Component{
    constructor(props)
    {
        super(props);

        const {bno} = queryString.parse(this.props.location.search);

        this.state = {
            bno : bno,
            board : {
                bno : bno,
                title : "",
                content : "",
                writer : "",
                regdate : ""
            }
        }
    
    }

    componentDidMount(){
        this.reloadModifyWebBoard(this.state.bno);
    }

    reloadModifyWebBoard(bno){
        ApiService.fetchModifyWebBoard(bno)
                    .then(res=>{
                        this.setState({
                            board : res.data
                        });
                    })
                    .catch(err=>{
                        console.log("fetchModifyWebBoard() error!", err);
                    });

    }

    render(){
        return (
            <>
            <div>Modify Page</div>
            <div>
                <div>
                    <label>Bno</label>
                    <input name="bno" defaultValue={this.state.board.bno} readOnly style={{background:'lightgray'}}/>
                </div>
                <div>
                    <label>Title</label>
                    <input name="title" defaultValue={this.state.board.title}/>
                </div>
                <div>
                    <label>Content</label>
                    <input name="content" defaultValue={this.state.board.content}/>
                </div>
                <div>
                    <label>Writer</label>
                    <input name="writer" defaultValue={this.state.board.writer} readOnly/>
                </div>
                <div>
                    <label>Regdate</label>
                    <input name="regdate" defaultValue={change_date(this.state.board.regdate)} readOnly/>
                </div>
            </div>
            </>
        );
    }
}

export default WebBoardModifyComponent;
