import ApiService from "../../ApiService";

class WebBoardViewComponent extends Component{
    constructor(props){
        super(props);

        this.state = {
            bno : this.props.location.state.bno
        }

        console.log("bno is " + this.state.bno);
    }

    componentDidMount(){    
        //this.reloadWebBoardView();
    }
    
    reloadWebBoardView(bno){
        ApiService.viewWebBoard(bno)
                    .then(res=>{
                        console.log(res);
                    })
                    .catch(err=>{
                        console.log("viewWebBoard() error!",err);
                    })
    }

    render(){
        return (
            <>
            hello view
            </>
        ); 
    }
}

export default WebBoardViewComponent;
