import { Component } from "react";
import {Modal} from 'react-responsive-modal';
import 'react-responsive-modal/styles.css';

class ReplyAddComponent extends Component{
    
    constructor(props){
        super(props);

        this.state = {
            openModal : false,
            replyText : "",
            replyer : ""
        }
    }

    onChangeReplyText = (e)=>{
        this.setState({replyText : e.target.value});
    }

    onChangeReplyer = (e)=>{
        this.setState({replyer : e.target.value});
    }

    onClickSave = ()=>{
        console.log("replyText is " + this.state.replyText);
        console.log("replyer is " + this.state.replyer);
    }

    onClickButton = (e)=>{
        e.preventDefault();
        this.setState({openModal:true});
    }

    onCloseModal = ()=>{
        this.setState({openModal:false});
    }

    render(){
        return(
            <div>
                <button onClick={this.onClickButton}>Add Reply</button>
                <Modal open={this.state.openModal} onClose={this.onCloseModal}>
                    <div>
                        <div>
                            <label>Reply Text</label>
                            <input type="text" name="replyText" onChange={this.onChangeReplyText}/>
                        </div>
                        <div>
                            <label>Replyer</label>
                            <input type="text" name="replyer" onChange={this.onChangeReplyer}/>
                        </div>
                        <div>
                            <button onClick={()=>{this.onClickSave()}}>Save</button>
                            <button onClick={()=>{this.onCloseModal()}}>Close</button>
                        </div>
                    </div>
                </Modal>
            </div>
        );
    }
}

export default ReplyAddComponent;
