import { Component } from "react";
import queryString from 'query-string';
import ApiService from "../../ApiService";
import change_date from "../../function/change_date";

class WebBoardModifyComponent extends Component{
    constructor(props)
    {
        super(props);

        const {bno} = queryString.parse(this.props.location.search);

        this.state = {
            bno : bno,
            board : {
                bno : bno,
                title : "",
                content : "",
                writer : "",
                regdate : ""
            }
        }
    
    }

    componentDidMount(){
        this.fetchModifyWebBoard(this.state.bno);
    }

    fetchModifyWebBoard(bno){
        ApiService.fetchModifyWebBoard(bno)
                    .then(res=>{
                        this.setState({
                            board : res.data
                        });
                    })
                    .catch(err=>{
                        console.log("fetchModifyWebBoard() error!", err);
                    });
    }

    onChangeTitle = (e)=>{
        this.setState({
            board : {
                bno : this.state.board.bno,
                title : e.target.value,
                content : this.state.board.content,
                writer : this.state.board.writer,
                regdate : this.state.board.regdate
            }
        });
    }

    onChangeContent = (e)=>{
        this.setState({
            board : {
                bno : this.state.board.bno,
                title : this.state.board.title,
                content : e.target.value,
                writer : this.state.board.writer,
                regdate : this.state.board.regdate
            }
        });
    }

    onClickModify = (board)=>{
        ApiService.modifyWebBoard(board)
                    .then(res=>{
                        if(res.data==="success")
                        {
                            alert("정상적으로 수정되었습니다.");
                            this.props.history.push("/boards/view?bno"+board.bno);
                        }
                        else
                        {
                            alert("등록이 실패하였습니다.");
                        }
                    })
                    .catch(err=>{
                        console.log("modifyWebBoard() error!",err);
                    });
    }

    render(){
        return (
            <>
            <div>Modify Page</div>
            <div>
                <div>
                    <label>Bno</label>
                    <input name="bno" defaultValue={this.state.board.bno} readOnly style={{background:'lightgray'}}/>
                </div>
                <div>
                    <label>Title</label>
                    <input name="title" defaultValue={this.state.board.title} onChange={this.onChangeTitle}/>
                </div>
                <div>
                    <label>Content</label>
                    <input name="content" defaultValue={this.state.board.content}/>
                </div>
                <div>
                    <label>Writer</label>
                    <input name="writer" defaultValue={this.state.board.writer} readOnly style={{background:'lightgray'}}/>
                </div>
                <div>
                    <label>Regdate</label>
                    <input name="regdate" defaultValue={change_date(this.state.board.regdate)} readOnly style={{background:'lightgray'}}/>
                </div>
            </div>
            <div>
                <button onClick={()=>{this.onClickModify(this.state.board)}}>Modify</button>
                <button>Delete</button>
                <button>Cancel & Go List</button>
            </div>
            </>
        );
    }
}

export default WebBoardModifyComponent;
